				// }

			}

				$tbl .= '
						<td style="text-align: right; font-size: 12px; border: 1px solid black;font-weight: bold; color: rgb(30,144,255);" width="20%">'
							.number_format($sommeTTC, 2, ',', ' ').
						'&nbsp;&nbsp;</td>
						';
			return $tbl;
