			$compteur = 0;
			$bg = '';
			$style = '';

			/*echo '<br> id_cli = '.$id_cli;
			echo '<br> id_mod_trans = '.$id_mod_trans;
			echo '<br> id_mod_lic = '.$id_mod_lic;
			echo '<br> num_lic = '.$num_lic;*/

