		public function getTailleCompteur($i){
			if(strlen($i) == '1' ){
				$i = '00'.$i;
			}else if(strlen($i) == '2' ){
				$i = '0'.$i;
			}else if(strlen($i) == '3' ){
				$i = ''.$i;
			}
			return $i;
		}
		
		public function getTailleCompteur2($i){
			if(strlen($i) == '1' ){
				$i = '000'.$i;
			}else if(strlen($i) == '2' ){
				$i = '00'.$i;
			}else if(strlen($i) == '3' ){
				$i = '0'.$i;
			}else if(strlen($i) == '4' ){
				$i = ''.$i;
			}
			return $i;
		}
		
		public function nbreSummary($statut, $id_mod_lic, $id_cli, $id_mod_trans, $commodity){
