			$requete = $connexion-> prepare("SELECT COUNT(ref_dos) AS nbre
											FROM dossier
											WHERE id_mod_lic = ?
												AND statut = ?
												$sqlClient
												$sqlModeTransport
												$sqlCommodity");
			$requete-> execute(array($entree['id_mod_lic'], $entree['statut']));
