			include('connexion.php');
			$compteur = 0;

			$requete = $connexion-> prepare("SELECT COUNT(id_dos) AS nbr_doublon, CONCAT(ref_fact,'_',road_manif) AS ref_fact_road_manif
												FROM dossier
												WHERE id_cli = ?
