			if (isset($id_cli) && ($id_cli!='')) {
				$sqlClient = ' AND fl.id_cli = '.$id_cli;
			}

			$sqlEtat = '';
			if (isset($etat) && ($etat!='')) {
				// if ($etat=='1') {
				// 	$sqlEtat = 'AND EXISTS (SELECT ref_fact FROM licence)';
				// }else{
				// 	$sqlEtat = 'AND NOT EXISTS (SELECT ref_fact FROM licence)';
