													cl.nom_cli AS nom_cli,
													DATEDIFF(CURRENT_DATE(), fl.date_fact_rec) AS delai,
													lic.num_lic AS num_lic,
													IF(lic.num_lic IS NOT NULL AND lic.num_lic NOT LIKE '%INV%',
