															CONCAT('<a href=\'#\' onclick=\"window.open(\'popUpMarchandiseDossierLicence.php?num_lic=',l.num_lic,'&couleur=\',\'Dossiers ',l.num_lic,'\',\'width=900,height=900\');\">',COUNT(md.id_march_dos),'</a>') AS nbre_dos,
