															(IF(l.fob IS NOT NULL, l.fob, 0)-IF(l.fob_apure IS NOT NULL, l.fob_apure, 0)) AS ecart,
															(((l.fob-l.fob_apure)*100)/l.fob) AS pourcentage_ecart
