													AND det.montant > 0
												GROUP BY d.id_deb
												ORDER BY d.rang ASC');
			$requete-> execute(array($entree['ref_fact'], $entree['id_t_deb']));
			while($reponse = $requete-> fetch()){

				if($reponse['tva'] == '0'){
					$tva = '0';
					$ttc = $reponse['ht'];
				}else{
					if ($ref_fact == 'DECKCC2200221' && $id_t_deb == '3') {
						$tva = 40;
						$ttc = $reponse['ht'] + 40;
					}else{
						$tva = round(($reponse['ht'] * 0.16), 2);
